userDefinedLinkHintCss:
div > .vimiumHintMarker {
  padding: 2px 3px;
  background-color: #282828;
  border: 0;
  border-radius: 2px;
  box-shadow: 0em 0.1em 0.6em 0.1em rgba(0, 0, 0, 0.4);
  background-image: none;
}

div > .vimiumHintMarker span {
  font-size: 18px;
  font-family: Hack Nerd Font;
  font-weight: bold;
  text-shadow: none;
  color: #458588;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #83a598;
}

/*VOMNIBAR*/
#vomnibar{
    background-color: #282828;
}

#vomnibar input {
  color: #ebdbb2;
  font-weight: normal;
  font-size: 20px;
  font-family: Hack Nerd Font;
  height: 34px;
  margin-bottom: 0;
  padding: 4px;
  background-color: #1d2021;
  border-radius: 3px;
  border: 1px solid #282828;
  box-shadow: #add8e6 0px 0px 1px;
  width: 100%;
  outline: none;
  box-sizing: border-box;
}

#vomnibar .vomnibarSearchArea {
  display: block;
  padding: 10px;
  background-color: #1d2021;
  border-radius: 4px 4px 0 0;
  border-bottom: 0px solid #add8e6;
}

#vomnibar ul {
  background-color: #282828;
  border-radius: 0 0 4px 4px;
  list-style: none;
  padding-top: 0;
  padding-bottom: 0;
  margin-block-start: 2px;
  margin-block-end: 0px;
}

#vomnibar li {
  border-bottom: 1px solid #1d2021;
  line-height: 1.1em;
  padding: 7px 10px;
  font-size: 16px;
  color: f8f8f2;
  position: relative;
  display: list-item;
  margin: auto;
}

#vomnibar li .vomnibarBottomHalf {
  font-size: 15px;
  margin-top: 3px;
  padding: 2px 0;
}

#vomnibar li .vomnibarSource {
  color: #689d6a;
  margin-right: 4px;
}
#vomnibar li .vomnibarRelevancy {
  position: absolute;
  right: 0;
  top: 0;
  padding: 5px;
  background-color: #282a36;
  color: #ebdbb2;
  font-family: Hack Nerd Font Mono;
  width: 100px;
  overflow: hidden;
}

#vomnibar li .vomnibarUrl {
   color: #83a598;
